import React from "react";
import { useParams } from "react-router-dom";

const ProjectDetailPage = () => {
  const { id } = useParams();

  // 임시 프로젝트 데이터 (실제 데이터는 API 호출로 대체)
  const projects = [
    {
      id: 1,
      title: "강남 역세권 주거시설",
      location: "서울시 강남구",
      expectedReturn: 12,
      period: "12개월",
      totalAmount: 100000000,
      currentAmount: 67000000,
      description:
        "서울 강남에 위치한 주거시설로, 안정적인 수익을 기대할 수 있습니다.",
      thumbnail: "https://via.placeholder.com/600x400",
    },
    {
      id: 2,
      title: "부산 해운대 레지던스",
      location: "부산시 해운대구",
      expectedReturn: 15,
      period: "18개월",
      totalAmount: 200000000,
      currentAmount: 150000000,
      description: "부산 해운대에 위치한 레지던스로, 관광 수익이 기대됩니다.",
      thumbnail: "https://via.placeholder.com/600x400",
    },
    // 더미 데이터 추가 가능
  ];

  // 선택한 프로젝트 찾기
  const project = projects.find((p) => p.id === parseInt(id));

  if (!project) {
    return <div>프로젝트를 찾을 수 없습니다.</div>;
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <img
            src={project.thumbnail}
            alt={project.title}
            className="w-full h-48 object-cover"
          />
          <div className="p-4">
            <h1 className="text-2xl font-bold text-gray-900">
              {project.title}
            </h1>
            <p className="text-gray-600">{project.location}</p>
            <div className="mt-4">
              <p className="text-gray-800">{project.description}</p>
              <div className="mt-4">
                <div className="flex justify-between">
                  <span className="text-gray-600">예상 수익률</span>
                  <span className="text-green-600 font-semibold">
                    {project.expectedReturn}%
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">투자 기간</span>
                  <span>{project.period}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">총 모집 금액</span>
                  <span>{project.totalAmount.toLocaleString()} 원</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">현재 모집 금액</span>
                  <span>{project.currentAmount.toLocaleString()} 원</span>
                </div>
              </div>
            </div>
            <button
              className="mt-4 w-full py-2 px-4 bg-green-600 text-white rounded-md hover:bg-green-700"
              onClick={() => alert("투자하기 기능 구현 예정")}
            >
              크라우드펀딩 시작
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProjectDetailPage;
